// Check if a cat is sleeping
if (cat.isSleeping()) {
    Chat.log("Cat is currently sleeping.");
} else {
    Chat.log("Cat is awake.");
}

// Update cat statistics based on sleep status
this.catStats.sleepingCats = cat.isSleeping() ? 1 : 0;
this.catStats.awakeCats = cat.isSleeping() ? 0 : 1;

// Get the collar color and its name/RGB value
const collarColor = cat.getCollarColor();
Chat.log(`Collar color name: ${collarColor.getName()}`);
Chat.log(`Collar color RGB: 0x${collarColor.getColorValue().toString(16).toUpperCase()}`);

// Get and display the cat's variant
const variantId = cat.getVariant();
const variantName = this.getVariantDisplayName(variantId); // Assuming getVariantDisplayName is available
Chat.log(`Cat variant: ${variantName} (${variantId})`);
