// Method 1: From entity events, check ghast shooting state upon spawn
JsMacros.on("EntitySpawn", JavaWrapper.methodToJavaAsync((event) => {
    const entity = event.getEntity();
    if (entity.is("minecraft:ghast")) {
        const ghast = entity; // Already properly typed
        Chat.log(`Ghast spawned! Shooting state: ${ghast.isShooting()}`);
    }
}));

// Method 2: From world queries, find ghasts and check their shooting state
const entities = World.getEntities();
for (const entity of entities) {
    if (entity.is("minecraft:ghast")) {
        const ghast = entity;
        const isAttacking = ghast.isShooting();
        if (isAttacking) {
            Chat.log(`Found a ghast that is currently shooting!`);
        }
    }
}

// Example 3: Monitor ghast's shooting state changes in a real-time system
// ghastData.entity is an EntityHelper which has been confirmed as a ghast
const ghast = ghastData.entity.as("minecraft:ghast");
const isShooting = ghast.isShooting();

if (ghastData.lastShootState !== isShooting) {
    ghastData.stateChanges++;
    ghastData.lastShootState = isShooting;

    const stateText = isShooting ? "SHOOTING" : "CALM";
    Chat.log(`&6ðŸ‘» ${ghastData.name} state changed to: ${stateText}`);

    if (isShooting && Player.getPlayer().distanceTo(ghastData.entity) <= 20) {
        this.sendAlert("Ghast ATTACKING - Take cover!", "danger");
    }
}
