const sharpness = new EnchantmentHelper("minecraft:sharpness");
const curse = new EnchantmentHelper("minecraft:vanishing_curse");

Chat.log(`Sharpness weight: ${sharpness.getWeight()}`);
Chat.log(`Curse weight: ${curse.getWeight()}`);

const curse = new EnchantmentHelper("minecraft:vanishing_curse");
const sharpness = new EnchantmentHelper("minecraft:sharpness");

Chat.log(`Is curse: ${curse.isCursed()}`); // Output: Is curse: true
Chat.log(`Is curse: ${sharpness.isCursed()}`); // Output: Is curse: false

const mending = new EnchantmentHelper("minecraft:mending");
const sharpness = new EnchantmentHelper("minecraft:sharpness");

Chat.log(`Is treasure: ${mending.isTreasure()}`); // Output: Is treasure: true
Chat.log(`Is treasure: ${sharpness.isTreasure()}`); // Output: Is treasure: false

const sharpness = new EnchantmentHelper("minecraft:sharpness");
Chat.log(sharpness.getRomanLevelName(3).getString()); // Output: Sharpness III
Chat.log(sharpness.getRomanLevelName(5000).getString()); // Output: Sharpness 5000

const sharpness = new EnchantmentHelper("minecraft:sharpness");
const smite = new EnchantmentHelper("minecraft:smite");
const knockback = new EnchantmentHelper("minecraft:knockback");

Chat.log(sharpness.isCompatible(smite)); // Output: false (conflict)
Chat.log(sharpness.isCompatible(knockback)); // Output: true (compatible)

// Using string ID
Chat.log(sharpness.isCompatible("minecraft:protection")); // Output: true (compatible)

const sharpness = new EnchantmentHelper("minecraft:sharpness");
const smite = new EnchantmentHelper("minecraft:smite");

Chat.log(sharpness.conflictsWith(smite)); // Output: true (conflict)
Chat.log(sharpness.conflictsWith("minecraft:protection")); // Output: false (compatible)

const sharpness = new EnchantmentHelper("minecraft:sharpness");
const compatible = sharpness.getCompatibleEnchantments();

Chat.log("Compatible enchantments:");
for (const enchant of compatible) {
    Chat.log(`- ${enchant.getName()}`);
}

const sharpness = new EnchantmentHelper("minecraft:sharpness");
const allCompatible = sharpness.getCompatibleEnchantments(true); // Ignore type restrictions
const typeCompatible = sharpness.getCompatibleEnchantments(false); // Respect type restrictions

const sharpness = new EnchantmentHelper("minecraft:sharpness");
const conflicting = sharpness.getConflictingEnchantments();

Chat.log("Conflicting enchantments:");
for (const enchant of conflicting) {
    Chat.log(`- ${enchant.getName()}`);
}

const sharpness = new EnchantmentHelper("minecraft:sharpness");
const sword = player.getMainHand();

if (sharpness.canBeApplied(sword)) {
    Chat.log("Sharpness can be applied to this item");
} else {
    Chat.log("Sharpness cannot be applied to this item");
}

const sharpness = new EnchantmentHelper("minecraft:sharpness");
const acceptableItems = sharpness.getAcceptableItems();

Chat.log("Items that can have Sharpness:");
for (const item of acceptableItems) {
    Chat.log(`- ${item.getId()}`);
}
