// Get basic information about a block
function analyzeBlock(blockData) {
    if (!blockData) {
        Chat.log("No block data available");
        return;
    }

    Chat.log(`Block Information:`);
    Chat.log(`  Position: ${blockData.getX()}, ${blockData.getY()}, ${blockData.getZ()}`);
    Chat.log(`  Type: ${blockData.getId()}`);
    Chat.log(`  Name: ${blockData.getName().getString()}`);
    Chat.log(`  Block State: ${JSON.stringify(blockData.getBlockState())}`);
}

// Complete block analysis
function fullBlockAnalysis(blockData) {
    if (!blockData) return;

    Chat.log("=== Complete Block Analysis ===");

    // Basic info
    Chat.log(`Position: [${blockData.getX()}, ${blockData.getY()}, ${blockData.getZ()}]`);
    Chat.log(`Block ID: ${blockData.getId()}`);
    Chat.log(`Display Name: ${blockData.getName().getString()}`);

    // Block state
    const state = blockData.getBlockState();
    if (Object.keys(state).length > 0) {
        Chat.log("Block State Properties:");
        Object.entries(state).forEach(([key, value]) => {
            Chat.log(`  ${key}: ${value}`);
        });
    }

    // Block helper info
    const blockHelper = blockData.getBlock();
    const blockStateHelper = blockData.getBlockStateHelper();

    Chat.log(`Block hardness: ${blockStateHelper.getHardness()}`);
    Chat.log(`Block luminance: ${blockStateHelper.getLuminance()}`);
    Chat.log(`Is solid: ${blockStateHelper.isSolid()}`);
    Chat.log(`Is air: ${blockStateHelper.isAir()}`);
    Chat.log(`Is liquid: ${blockStateHelper.isLiquid()}`);
    Chat.log(`Tool required: ${blockStateHelper.isToolRequired()}`);

    // NBT data
    const nbt = blockData.getNBT();
    if (nbt) {
        Chat.log("Has NBT data (tile entity present)");
    }

    Chat.log("=== End Analysis ===\n");
}

