    Chat.log(`Tool required: ${blockStateHelper.isToolRequired()}`);

    // NBT data
    const nbt = blockData.getNBT();
    if (nbt) {
        Chat.log("Has NBT data (tile entity present)");
    }

    Chat.log("=== End Analysis ===\n");
}

