const configManager = JsMacros.getConfig();

// Get core configuration options
const coreConfig = configManager.getOptions(CoreConfigV2);
if (coreConfig) {
    Chat.log(`Current default profile: ${coreConfig.defaultProfile}`);
    Chat.log(`Ignored events: ${coreConfig.anythingIgnored}`);

    // Access profile information
    const profiles = coreConfig.profileOptions();
    profiles.forEach(profile => {
        Chat.log(`Available profile: ${profile}`);
    });
}

const configManager = JsMacros.getConfig();

// Add a custom option class (advanced usage)
try {
    configManager.addOptions("myMod", MyModConfigClass);
    Chat.log("Custom options added successfully");
} catch (error) {
    Chat.logColor(`&cFailed to add options: ${error.message}`);
}

const configManager = JsMacros.getConfig();
const CoreConfigV2 = Packages.xyz.wagyourtail.jsmacros.core.config.CoreConfigV2;

// Reload configuration from files
try {
    configManager.loadConfig();
    Chat.log("Configuration reloaded successfully");

    const coreConfig = configManager.getOptions(CoreConfigV2);
    Chat.log(`Loaded profiles: ${coreConfig.profileOptions()}`);
} catch (error) {
    Chat.logColor(`&cFailed to load configuration: ${error.message}`);
}

const configManager = JsMacros.getConfig();

// Save current configuration to file
configManager.saveConfig();
Chat.log("Configuration saved successfully");

const configManager = JsMacros.getConfig();

// Reload raw configuration data
try {
    configManager.reloadRawConfigFromFile();
    Chat.log("Raw configuration reloaded");
} catch (error) {
    Chat.logColor("&cFailed to reload raw config:", error.message);
}

const configManager = JsMacros.getConfig();

// Convert configuration format (usually handled internally)
try {
    configManager.convertConfigFormat();
    Chat.log("Configuration format converted successfully");
} catch (error) {
    Chat.logColor("&cFailed to convert configuration:", error.message);
}

// Convert specific class configuration
try {
    configManager.convertConfigFormat(MyConfigClass);
    Chat.log("MyConfigClass configuration converted");
} catch (error) {
    Chat.logColor("&cFailed to convert MyConfigClass:", error.message);
}

const configManager = JsMacros.getConfig();

// Reset to default configuration
try {
    configManager.loadDefaults();
    configManager.saveConfig();
    Chat.log("Configuration reset to defaults");
} catch (error) {
    Chat.logColor(`&cFailed to load defaults: ${error.message}`);
}
