        const isShoulder = parrot.isSittingOnShoulder();

        Chat.log(`  Status: ${isTamed ? "Tamed" : "Wild"}, ${isDancing ? "Dancing ðŸŽµ" : ""}${isFlying ? "Flying" : ""}${isShoulder ? "Shoulder-perched" : ""}`);
    }
});

// Display variant collection summary
Chat.log(`\nðŸ“Š Found ${parrots.length} parrots within ${range} blocks:`);
