// Example: Check if an event is cancellable
if (event.cancellable()) {
    Chat.log("This event can be cancelled");
} else {
    Chat.log("This event cannot be cancelled");
}

// Example: Check if an event is joinable
if (event.joinable()) {
    Chat.log("This event can be joined with other events");
}

// Example: Cancel a cancellable event
if (event.cancellable()) {
    event.cancel();
    Chat.log("Event cancelled successfully");
} else {
    Chat.log("Cannot cancel this event");
}

// Example: Check if event is already cancelled
if (event.isCanceled()) {
    Chat.log("Event was already cancelled");
    return; // Exit early
}

// Example: Get and use the event name
const eventName = event.getEventName();
Chat.log(`Handling event: ${eventName}`);

// Example: Dynamic event handling
switch (event.getEventName()) {
    case "Sound":
        handleSoundEvent(event);
        break;
    case "Tick":
        handleTickEvent(event);
        break;
    default:
        Chat.log(`Unknown event: ${eventName}`);
}

// Example: Manually trigger an event
event.trigger();
Chat.log("Event triggered manually");
