const inventory = Player.getInventory();
if (inventory.getType() === "Cartography Table") {
    const mapItem = inventory.getMapItem();
    if (!mapItem.isEmpty()) {
        Chat.log("Map loaded: " + mapItem.getName().getString());
    }
}

const inventory = Player.getInventory();
if (inventory.getType() === "Cartography Table") {
    const material = inventory.getMaterial();
    Chat.log("Material slot contains: " + material.getName().getString());
}

const inventory = Player.getInventory();
if (inventory.getType() === "Cartography Table") {
    const output = inventory.getOutput();
    if (!output.isEmpty()) {
        Chat.log("Result ready: " + output.getName().getString());
        inventory.quick(2); // Take result
    }
}
