const ingredients = recipe.getIngredients();
for (let i = 0; i < ingredients.length; i++) {
    const slot = ingredients[i];
    Chat.log(`Slot ${i} accepts:`, slot.map(item => item.getName()));
}

const output = recipe.getOutput();
Chat.log("Produces:", output.getName());
Chat.log("Quantity:", output.getCount());

// Craft one set
recipe.craft(false);

// Craft as many as possible
recipe.craft(true);

// Chain multiple operations
recipe.craft(false).craft(true);

const group = recipe.getGroup();
if (group) {
    Chat.log("Recipe group:", group);
}

if (recipe.hasRecipeRemainders()) {
    Chat.log("This recipe has remainder items");
}

const remainders = recipe.getRecipeRemainders();
for (const remainderList of remainders) {
    Chat.log("Possible remainders:", remainderList.map(item => item.getName()));
}

const recipeType = recipe.getType();
Chat.log("Recipe type:", recipeType);
switch (recipeType) {
    case "minecraft:crafting_shaped":
        Chat.log("Shaped crafting recipe");
        break;
    case "minecraft:crafting_shapeless":
        Chat.log("Shapeless crafting recipe");
        break;
    case "minecraft:smelting":
        Chat.log("Smelting recipe");
        break;
}

if (recipe.canCraft()) {
    Chat.log("You can craft this recipe!");
    recipe.craft(false);
} else {
    Chat.log("Insufficient materials");
}

const desiredAmount = 10;
if (recipe.canCraft(desiredAmount)) {
    Chat.log(`You can craft ${desiredAmount} of this recipe`);
} else {
    Chat.log(`Insufficient materials for ${desiredAmount} sets`);
}

const maxCraftable = recipe.getCraftableAmount();
Chat.log(`You can craft ${maxCraftable} sets of this recipe`);

if (maxCraftable > 0) {
    recipe.craft(true); // Craft as many as possible
}
