const bossBar = event.bossBar;
const uuid = bossBar.getUUID();
Chat.log(`Boss bar UUID: ${uuid}`);

// Use UUID to track specific boss bars
if (uuid === "12345678-1234-1234-1234-123456789abc") {
    Chat.log("This is the ender dragon boss bar!");
}

const bossBar = event.bossBar;
const percent = bossBar.getPercent();
const percentDisplay = (percent * 100).toFixed(1);

Chat.log(`Boss bar progress: ${percentDisplay}%`);

// Example: Alert when boss is below certain health
if (percent < 0.25) {
    Chat.log("WARNING: Boss is below 25% health!");
}

const bossBar = event.bossBar;
const color = bossBar.getColor();
Chat.log(`Boss bar color: ${color}`);

// React to different boss bar colors
switch (color) {
    case "RED":
        Chat.log("This appears to be an aggressive boss");
        break;
    case "BLUE":
        Chat.log("This might be a water-related boss");
        break;
    case "GREEN":
        Chat.log("This could be a nature-themed boss");
        break;
}

const bossBar = event.bossBar;
const style = bossBar.getStyle();
Chat.log(`Boss bar style: ${style}`);

// Handle different bar styles
if (style === "NOTCHED_6") {
    Chat.log("This boss bar shows 6 segments");
} else if (style === "NOTCHED_20") {
    Chat.log("This boss bar shows 20 segments (detailed progress)");
} else if (style === "PROGRESS") {
    Chat.log("This is a solid progress bar");
}

const bossBar = event.bossBar;
const colorValue = bossBar.getColorValue();

if (colorValue !== -1) {
    Chat.log(`Boss bar RGB color: ${colorValue}`);
    Chat.log(`Hex color: #${colorValue.toString(16).padStart(6, '0')}`);
} else {
    Chat.log("Boss bar color doesn't have a specific RGB value");
}

const bossBar = event.bossBar;
const colorFormat = bossBar.getColorFormat();

// Get formatting details
Chat.log(`Color format: ${colorFormat.getName()}`);
Chat.log(`Color code: ${colorFormat.getCode()}`);

// Use the format for text styling
const formattedText = colorFormat.format("Boss Fight!");
Chat.log(formattedText); // Will appear in boss bar color

const bossBar = event.bossBar;
const name = bossBar.getName();
const nameString = name.getString();

Chat.log(`Boss bar name: ${nameString}`);

// Check for specific boss names
if (nameString.toLowerCase().includes("ender dragon")) {
    Chat.log("Ender Dragon boss bar detected!");
} else if (nameString.toLowerCase().includes("wither")) {
    Chat.log("Wither boss bar detected!");
}

// Get JSON representation
const nameJson = name.getJson();
Chat.log("Boss bar name JSON:", nameJson);

const bossBar = event.bossBar;
Chat.log(bossBar.toString());
// Example output: BossBarHelper:{"name:": "Ender Dragon", "percent": 0.750000}
