const changed = modeButton.setValue("Hard");
if (changed) {
    Chat.log("Difficulty changed to Hard");
} else {
    Chat.log("Difficulty was already Hard");
}

// Cycle forward by 2 steps
modeButton.cycle(2);

// Cycle backward by 1 step
