const video = JsMacros.getOptions().getVideoOptions();

// Set for 60Hz monitor
video.setMaxFps(60);
JsMacros.getOptions().saveOptions();
Chat.log("FPS limited to 60");

// Set for competitive gaming (144Hz)
video.setMaxFps(144);
JsMacros.getOptions().saveOptions();
