const inv = Inventory.create();
if (inv && inv.getType() === "Crafting Table") {
    const grid = inv.getInput();
    for (let x = 0; x < 3; x++) {
        for (let y = 0; y < 3; y++) {
            const item = grid[x][y];
            if (!item.isEmpty()) {
                Chat.log(`Item at ${x},${y}: ${item.getName()}`);
            }
        }
    }
}
