const player = Player.getPlayer();
const advancementManager = player.getAdvancementManager();
for (const advancement of advancementManager.getAdvancements()) {
  const progress = advancement.getProgress();

  Chat.log(`Progress: ${(progress.getPercentage() * 100).toFixed(1)}%`);
  Chat.log(`Completed: ${progress.isDone()}`);
  Chat.log(`Requirements met: ${progress.countObtainedRequirements()}`);

  // Get obtained criteria
  const obtained = progress.getObtainedCriteria();
  if (obtained.length > 0) {
    Chat.log(`Obtained criteria: ${obtained.join(", ")}`);
    break;
  }
}

const player = Player.getPlayer();
const advancementManager = player.getAdvancementManager();
for (const advancement of advancementManager.getAdvancements()) {
  // TODO: Currently the toJson function of this causes "Advancement completion requirements did not exactly match specified criteria. Missing: []. Unknown: [has_cobblestone, has_the_recipe]"
