const player = Player.getPlayer();
const advancementManager = player.getAdvancementManager();
const progressMap = advancementManager.getAdvancementsProgress();
for (const [advancement, progress] of progressMap) {
  Chat.log(`${advancement.getId()}: ${progress.getPercentage() * 100}% complete`);

  if (progress.isDone()) {
    Chat.log(`  ✓ Fully completed`);
  } else if (progress.isAnyObtained()) {
    Chat.log(`  ⟳ In progress`);
  } else {
    Chat.log(`  ○ Not started`);
  }
}

const player = Player.getPlayer();
const advancementManager = player.getAdvancementManager();
const progress =
  advancementManager.getAdvancementProgress("story/obtain_armor");
Chat.log(`Progress percentage: ${progress.getPercentage() * 100}%`);
Chat.log(
  `Criteria completed: ${progress.getCompletedCriteria().length}/${progress.getTotalCriteria()}`,
);
Chat.log(`Is completed: ${progress.isDone()}`);

// Get individual criteria progress
const criteria = progress.getCriteria();
for (const [criterionName, completed] of Object.entries(criteria)) {
  Chat.log(`  ${criterionName}: ${completed ? "✓" : "○"}`);
}

