const player = Player.getPlayer();
const advancementManager = player.getAdvancementManager();
const advancements = advancementManager.getAdvancements();
for (const advancement of advancements) {
  const progress = advancement.getProgress();
  const criteria = progress.getCriteria();
  for (const [id, timestamp] of criteria) {
    const date = new Date(timestamp);
    Chat.log(`Criteria "${id}" completed on ${date.toLocaleDateString()}`);
    // e.g. 'Criteria "has_quartz" completed on 11/21/2025'
  }
}

