}

const player = Player.getPlayer();
const advancementManager = player.getAdvancementManager();
const advancement = advancementManager.getAdvancement(
  "minecraft:adventure/adventuring_time",
);
const progress = advancement.getProgress();
const requirements = advancement.getRequirements();
for (const criteriaList of requirements) {
  const timestamp = progress.getCriterionProgress(criteriaList[0]);
  if (timestamp !== -1) {
    const date = new Date(timestamp);
    Chat.log(
      `Adventure time criteria '${criteriaList[0]}' completed on ${date.toLocaleDateString()}`,
    );
  }
}

const player = Player.getPlayer();
const advancementManager = player.getAdvancementManager();
const advancement = advancementManager.getAdvancement(
  "minecraft:adventure/adventuring_time",
);
const progress = advancement.getProgress();
const requirements = advancement.getRequirements();
for (const criteriaList of requirements) {
  const isObtained = progress.isCriteriaObtained(criteriaList[0]);
  if (isObtained) {
    Chat.log(`Adventure time criteria '${criteriaList[0]}' is obtained`);
  }
}

