const listener = JsMacros.on(
  "BlockUpdate",
  JavaWrapper.methodToJava((event) => {
    const blockData = event.block;
    const state = blockData.getBlockState();
    if (state.facing) {
      Chat.log(`Block faces: ${state.facing}`);
    }
    if (state.waterlogged === "true") {
      Chat.log("Block is waterlogged");
    }
  }),
);

