const listener = JsMacros.on(
  "BlockUpdate",
  JavaWrapper.methodToJava((event) => {
    const blockData = event.block;
    const nbt = blockData.getNBT();
    if (!nbt) return;

    // On any server (including integrated), this will only return items for placed items that have nbt data
    // e.g., a shulker box or ctrl+middle mouse clicked chest being placed.
    const items = nbt.get("Items") || [];
    if (blockData.getNBT()) {
      Chat.log(
        `Container block placed - has NBT data. Found ${items.length()} items`,
      );
    }
  }),
);

