const inventory = Player.getInventory();
const helmet = inventory.getHelmet();
if (!helmet.isEmpty()) {
    Chat.log("Helmet: " + helmet.getName().getString());
}

const inventory = Player.getInventory();
const offhand = inventory.getOffhand();
if (!offhand.isEmpty()) {
    Chat.log("Offhand: " + offhand.getName().getString());
}

if (inventory.isInHotbar(slotUnderMouse)) {
    Chat.log("This is a hotbar slot");
}

const output = inventory.getOutput();
if (!output.isEmpty()) {
    Chat.log("Can craft: " + output.getName().getString());
}

const recipes = inventory.getCraftableRecipes();
for (const recipe of recipes) {
    Chat.log("Can craft: " + recipe.getOutput().getName().getString());
}

if (inventory.is("Survival Inventory")) {
    Chat.log("Player inventory is open");
}

const item = inventory.getSlot(36);
if (!item.isEmpty()) {
    Chat.log("Item: " + item.getName().getString());
}

const slot = inventory.getSlotUnderMouse();
if (slot >= 0) {
    const item = inventory.getSlot(slot);
}

const itemCounts = inventory.getItemCount();
for (const [itemId, count] of Object.entries(itemCounts)) {
    Chat.log(itemId + ": " + count);
}
